public class SymbolsAndCodes {    public static void main(String[] args) {        char ch;        int i;        for (i = 0; i <= 512; i++) {            ch = (char) i;            System.out.println(i + " - " + ch);        }        for (ch = 'А'; ch <= 'я'; ch++) {            int code = (int) ch;            System.out.println(ch + " - " + code);        }        ch = 'Ё';        i = (int)ch;        System.out.println(ch + " - " + i);        ch = 'ё';        i = (int)ch;        System.out.println(ch + " - " + i);    }}